import styled from "styled-components";

import * as C from "@styled/common/components";

export const Statuses = styled(C.Container)`
	padding: 6rem 0;
`;

export const StatusTree = styled.div``;

export const Status = styled.div`
	position: relative;

	&:not(:nth-child(1)) {
		margin-top: 2.6rem;
	}
`;

const STATUS_LEFT_OFFSET = "6rem";
const STATUS_DOT_SIZE = "1.2rem";

export const StatusTitle = styled.p`
	font-weight: 700;
	font-size: 2.4rem;
	position: relative;
	padding-left: ${STATUS_LEFT_OFFSET};
	font-family: ${({ theme }) => theme.font.heading};

	&:before {
		content: "";
		left: 0;
		width: ${STATUS_DOT_SIZE};
		height: ${STATUS_DOT_SIZE};
		border-radius: 50%;
		position: absolute;
		top: calc(50% - ${STATUS_DOT_SIZE} / 2);
		background: ${({ theme }) => theme.colors.primary};
	}
`;

export const StatusProgressLine = styled.div`
	left: 0;
	top: ${STATUS_DOT_SIZE};
	width: ${STATUS_DOT_SIZE};
	position: absolute;
	height: calc(100% - ${STATUS_DOT_SIZE} * 2);

	&:after {
		content: "";
		top: 0;
		width: 1px;
		height: 100%;
		position: absolute;
		left: calc(50% - 0.5px);
		background: ${({ theme }) => theme.colors.primary};
	}
`;

export const StatusDescription = styled.p`
	font-weight: 600;
	margin-top: 1rem;
	line-height: 180%;
	font-size: 1.4rem;
	padding-left: ${STATUS_LEFT_OFFSET};
`;
